import React, { Component } from 'react';
import { StyleSheet, TouchableOpacity, ToastAndroid, Platform, RefreshControl, View } from 'react-native';
import { Container, Header, Left, Body, Right, Button, Icon, Title, Item, Input } from 'native-base';
import { Avatar, Text,  Image } from 'react-native-elements';
import { ActivityIndicator, Dimensions } from 'react-native';
import { Divider } from 'react-native-paper';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import { TouchableHighlight, TouchableNativeFeedback, FlatList } from 'react-native-gesture-handler';
import Feeds from '../../Demo/Home.json';
import Moment from 'moment';

var {width, height} = Dimensions.get('window');


export default class HomeScreen extends Component {

    constructor(props){
      super(props);
      this.state = { refreshing: false };
      Moment.locale('en');
    }

    searchPage = () => {
      ToastAndroid.show("initial", ToastAndroid.SHORT)
    }

    onRefresh() {
      console.log('make ur api call');
    }

    renderSeparator = () => {
      return (
        <View
          style={{
            height: 1,
            width: "86%",
            backgroundColor: "#CED0CE",
            marginLeft: "14%"
          }}
        />
      );
    };

  render() {
    return (
      <Container>
        <FlatList 
          style={{ height: 100 }}
            data={Feeds}
            renderItem={({ item, index }) =>
              <FeedItem feed={item} />
            }
            refreshControl={
                <RefreshControl
                  //refresh control used for the Pull to Refresh
                  refreshing={this.state.refreshing}
                  onRefresh={this.onRefresh.bind(this)}
                />
              }
          />
      </Container>
    )
  }
}


const FeedItem = ({feed}) => {
  return (
      <TouchableOpacity onPress={()=> 
          ToastAndroid.show(feed.title, ToastAndroid.LONG)
      }>
      <View style={{paddingTop: 10, height: 'auto' ,elevation: 1, width: width ,height: height,flex: 1, flexDirection: 'column', backgroundColor: '#c9c9c9' }} >
          <View style={{ height: 90, flex: 1 }}>
            <Container style={{ display: 'flex', flexDirection: 'row', justifyContent :'flex-start', paddingTop:20, paddingLeft: 15, paddingRight: 15}}>
                <View style={{ }}>
                    <Avatar
                        rounded
                        size='medium'
                        source={{
                            uri:
                            feed.userId.profileImage,
                        }}
                    />
                </View>
                <View style={{  marginLeft: 20 }}>
                    <Text style={{  textAlign: 'justify', fontWeight: 'bold', color: '#08070D'}} numberOfLines={1}>
                        {feed.userId.first_name}
                    </Text>
                    <Text style={{  textAlign: 'justify', fontSize: 12}} numberOfLines={1}>
                        {feed.userId.tagline}
                    </Text>
                    <Text style={{  textAlign: 'justify', fontSize: 12, color: 'grey'}} numberOfLines={1}>
                        {Moment(feed.published_at).fromNow()}
                    </Text>
                </View>
            </Container>
          </View>
          <View style={{ height: 'auto' }}>
            <Container style={{ display: 'flex', flexDirection: 'row', justifyContent :'flex-start', paddingTop:0, paddingLeft: 10, paddingRight:10}}>
                <View style={{  marginLeft: 10 }}>
                    <Text style={{  textAlign: 'auto', fontSize: 13}} >
                        {feed.post_descripltion}
                    </Text>
                </View>
            </Container>
          </View>
      </View>
      </TouchableOpacity>
  )
}




const styles = StyleSheet.create({
    container: {
      padding: 0
    },
    textColor: {
        color: 'black'
    },

});
